         exe = os.path.join(p, name)
         if is_executable(exe):
             return os.path.abspath(exe)
         for ext in [""] + os.environ.get("PATHEXT", "").split(os.pathsep):
             exe = "{}{}".format(exe, ext.lower())
             if is_executable(exe):
                 return os.path.abspath(exe)

def weighted_random_choice(items):
     l = list(items)
     r = random.random() * sum([i[1] for i in l])
     for x, p in l:
         if p > r:
             return x
         r -= p
     return None

def strip_url_params3(url, strip=None):
     if not strip: strip = []
     parse = urllib.parse.urlparse(url)
     query = urllib.parse.parse_qs(parse.query)
     query = {k: v[0] for k, v in query.items() if k not in strip}
     query = urllib.parse.urlencode(query)
     new = parse._replace(query=query)
     return new.geturl()

def sphere_pick_polar(d, n=1, rng=None):
     if rng is None:
         rng = np.random
     a = np.empty([n, d])
     if d == 1:
         a[:, 0] = rng.randint(2, size=n) * 2 - 1
     elif d == 2:
         a[:, 0] = 1.0
         a[:, 1] = rng.uniform(-np.pi, +np.pi, n)
     elif d == 3:
         u, v = rng.uniform(0.0, 1.0, (2, n))
         a[:, 0] = 1.0
         a[:, 1] = np.arccos(2.0 * v - 1.0)
         a[:, 2] = 2.0 * np.pi * u
     else:
         raise Exception("Invalid vector for polar representation")
     return a

