def get_prob(self):
         if self.total < 5:
             return 0.
         a, b = self.dup + 1, self.nodup + 1
         n = a + b
         p = a / n
         q = b / n
         return p - 1.96 * math.sqrt(p * q / n)

def timed(func):
     @functools.wraps(func)
     def timer(*args, **kwargs):
         start_time = time()
         result = func(*args, **kwargs)
         elapsed_time = str(timedelta(seconds=int(time() - start_time)))
         print("Elapsed time is {}".format(elapsed_time))
         return result
     return timer

