         )

def _encode_str(self, obj, escape_quotes=True):
         def replace(match):
             s = match.group(0)
             try:
                 if escape_quotes:
                     return ESCAPE_DCT[s]
                 else:
                     return BASE_ESCAPE_DCT[s]
             except KeyError:
                 n = ord(s)
                 if n < 0x10000:
                     return "\\u{0:04x}".format(n)
                 else:
                     n -= 0x10000
                     s1 = 0xd800   ((n >> 10) & 0x3ff)
                     s2 = 0xdc00   (n & 0x3ff)
                     return "\\u{0:04x}\\u{1:04x}".format(s1, s2)
         if escape_quotes:
             return 
         else:
             return BASE_ESCAPE_ASCII.sub(replace, obj)

def create_cookie(self, value, typ, cookie_name=None, ttl=-1, kill=False):
         if kill:
             ttl = -1
         elif ttl < 0:
             ttl = self.default_value["max_age"]
         if cookie_name is None:
             cookie_name = self.default_value["name"]
         c_args = {}
         srvdomain = self.default_value["domain"]
         if srvdomain and srvdomain not in ["localhost", "127.0.0.1",
                                            "0.0.0.0"]:
             c_args["domain"] = srvdomain
         srvpath = self.default_value["path"]
         if srvpath:
             c_args["path"] = srvpath
         timestamp = str(int(time.time()))
         try:
             cookie_payload = "::".join([value, timestamp, typ])
         except TypeError:
             cookie_payload = "::".join([value[0], timestamp, typ])
         cookie = make_cookie(
             cookie_name, cookie_payload, self.sign_key,
             timestamp=timestamp, enc_key=self.enc_key, max_age=ttl,
             sign_alg=self.sign_alg, **c_args)
         return cookie

def randbelow(num: int) -> int:
     if not isinstance(num, int):
         raise TypeError("number must be an integer")
     if num <= 0:
         raise ValueError("number must be greater than zero")
     if num == 1:
         return 0
     nbits = num.bit_length()    
     randnum = random_randint(nbits)    
     while randnum >= num:
         randnum = random_randint(nbits)
     return randnum

def extractdata(pattern, text=None, filepath=None):
     y = []
     if text is None:
         textsource = open(filepath, "r")
     else:
         textsource = text.splitlines()
     for line in textsource:
         match = scanf(pattern, line)
         if match:
             if len(y) == 0:
                 y = [[s] for s in match]
             else:
                 for i, ydata in enumerate(y):
                     ydata.append(match[i])
     if text is None:
         textsource.close()
     return y

def normal_distribution(self, pos, sample):
         curr_sigma_sq = self.sigma_sq(sample)
         delta = self.trial_history[pos - 1] - self.f_comb(pos, sample)
         return np.exp(np.square(delta) / (-2.0 * curr_sigma_sq)) / np.sqrt(2 * np.pi * np.sqrt(curr_sigma_sq))

