def copy_file(src, dst, ignore=None):
    src = re.sub("[^\w/\-\.\*]", "", src)
    dst = re.sub("[^\w/\-\.\*]", "", dst)
    if len(re.sub("[\W]", "", src)) < 5 or len(re.sub("[\W]", "", dst)) < 5:
       debug.log("Error: Copying file failed. Provided paths are invalid! src="%s" dst="%s""%(src, dst))
    else:
       check = False
       if dst[-1] == "/":
          if os.path.exists(dst):
             check = True 
          else:
             debug.log("Error: Copying file failed. Destination directory does not exist (%s)"%(dst)) 
       elif os.path.exists(dst):
          if os.path.isdir(dst):
             check = True 
             dst += "/" 
          else:
             debug.log("Error: Copying file failed. %s exists!"%dst)
       elif os.path.exists(os.path.dirname(dst)):
          check = True 
       else:
          debug.log("Error: Copying file failed. %s is an invalid distination!"%dst)
       if check:
          files = glob.glob(src)
          if ignore is not None: files = [fil for fil in files if not ignore in fil]
          if len(files) != 0:
             debug.log("Copying File(s)...", "Copy from %s"%src, "to %s"%dst) 
             for file_ in files:
                if os.path.isfile(file_):
                   debug.log("Copying file: %s"%file_) 
                   shutil.copy(file_, dst)
                else:
                   debug.log("Error: Copying file failed. %s is not a regular file!"%file_) 
          else: debug.log("Error: Copying file failed. No files were found! (%s)"%src)

def _convert(value, default=None, convert=None):
     def convert_bool(value):
         if value.lower() in ("true", "t", "yes", "y"):
             return True
         if value.lower() in ("false", "f", "no", "n"):
             return False
         raise ValueError("{} cannot be converted to bool".format(value))
     if value == "":
         value = None
     if convert is None:
         if default is not None:
             convert = type(default)
         else:
             convert = str
     if convert == bool:
         convert = convert_bool
     if value is None:
         return default
     else:
         return convert(value)

def filter(this, args):
         array = to_object(this, args.space)
         callbackfn = get_arg(args, 0)
         arr_len = js_arr_length(array)
         if not is_callable(callbackfn):
             raise MakeError("TypeError", "callbackfn must be a function")
         _this = get_arg(args, 1)
         k = 0
         res = []
         while k < arr_len:
             if array.has_property(unicode(k)):
                 kValue = array.get(unicode(k))
                 if to_boolean(
                         callbackfn.call(_this, (kValue, float(k), array))):
                     res.append(kValue)
             k += 1
         return args.space.ConstructArray(res)

