def __mul__(self, other):
         if self.finalized:
             dmat = self.__class__()
             dmat.shape = self.shape
             if isinstance(other, Sparse3DMatrix):  
                 if other.finalized:
                     for hid in xrange(self.shape[1]):
                         dmat.data.append(self.data[hid].multiply(other.data[hid]))
                 else:
