def copy(configfile="", destpath="", overwrite=False, sub_node=""):
     log.info("Running {0} {1} {2}".format(os.path.basename(__file__),
                                           whoami(),
                                           locals()))
     assert(os.path.isfile(configfile))
     if os.path.exists(destpath):
         if os.listdir(destpath):
             raise FolderAlreadyExists("Folder {0} already exists. Please clean "
                                       "it or change destpath.".format(destpath))
     else:
         log.info("Creating folder {0}".format(destpath))
         path(destpath).makedirs_p()
     from boyle.files.file_tree_map import FileTreeMap
     file_map = FileTreeMap()
     try:
         file_map.from_config_file(configfile)
     except Exception as e:
         raise FileTreeMapError(str(e))
     if sub_node:
         sub_map = file_map.get_node(sub_node)
         if not sub_map:
             raise FileTreeMapError("Could not find sub node "
                                    "{0}".format(sub_node))
         file_map._filetree = {}
         file_map._filetree[sub_node] = sub_map
     try:
         file_map.copy_to(destpath, overwrite=overwrite)
     except Exception as e:
         raise FileTreeMapError(str(e))

def _assert_tag_and_attributes_are_equal(xml1, xml2, can_extend=False):
     if xml1.tag != xml2.tag:
         raise AssertionError(u"Tags do not match: {tag1} != {tag2}".format(
             tag1=_describe_element(xml1), tag2=_describe_element(xml2)
         ))
     added_attributes = set(xml2.attrib).difference(xml1.attrib)
     missing_attributes = set(xml1.attrib).difference(xml2.attrib)
     if missing_attributes:
         raise AssertionError(u"Second xml misses attributes: {path}/({attributes})".format(
             path=_describe_element(xml2), attributes=",".join(missing_attributes)
         ))
     if not can_extend and added_attributes:
         raise AssertionError(u"Second xml has additional attributes: {path}/({attributes})".format(
             path=_describe_element(xml2), attributes=",".join(added_attributes)
         ))
     for attrib in xml1.attrib:
         if not _xml_compare_text(xml1.attrib[attrib], xml2.attrib[attrib], False):
             raise AssertionError(u"Attribute values are not equal: {path}/{attribute}["{v1}" != "{v2}"]".format(
                 path=_describe_element(xml1), attribute=attrib, v1=xml1.attrib[attrib], v2=xml2.attrib[attrib]
             ))
     if not _xml_compare_text(xml1.text, xml2.text, True):
         raise AssertionError(u"Tags text differs: {path}["{t1}" != "{t2}"]".format(
             path=_describe_element(xml1), t1=xml1.text, t2=xml2.text
         ))
     if not _xml_compare_text(xml1.tail, xml2.tail, True):
         raise AssertionError(u"Tags tail differs: {path}["{t1}" != "{t2}"]".format(
             path=_describe_element(xml1), t1=xml1.tail, t2=xml2.tail
         ))

def normal_distribution(mean, variance,
                         minimum=None, maximum=None, weight_count=23):
     standard_deviation = math.sqrt(variance)
     min_x = (standard_deviation * -5) + mean
     max_x = (standard_deviation * 5) + mean
     step = (max_x - min_x) / weight_count
     current_x = min_x
     weights = []
     while current_x < max_x:
         weights.append(
             (current_x, _normal_function(current_x, mean, variance))
         )
         current_x += step
     if minimum is not None or maximum is not None:
         return bound_weights(weights, minimum, maximum)
     else:
         return weights

def levenshtein_similarity(s1, s2):
     conc = pandas.Series(list(zip(s1, s2)))
     def levenshtein_apply(x):
         try:
             return 1 - jellyfish.levenshtein_distance(x[0], x[1]) \
                 / np.max([len(x[0]), len(x[1])])
         except Exception as err:
             if pandas.isnull(x[0]) or pandas.isnull(x[1]):
                 return np.nan
             else:
                 raise err
     return conc.apply(levenshtein_apply)

