def toUIntArray(img, dtype=None, cutNegative=True, cutHigh=True, 
                 range=None, copy=True): 
     mn, mx = None, None 
     if range is not None: 
         mn, mx = range 
     if dtype is None: 
         if mx is None: 
             mx = np.nanmax(img) 
         dtype = np.uint16 if mx > 255 else np.uint8 
     dtype = np.dtype(dtype) 
     if dtype == img.dtype: 
         return img 
     b = {"uint8": 255, 
          "uint16": 65535, 
          "uint32": 4294967295, 
          "uint64": 18446744073709551615}[dtype.name] 
     if copy: 
         img = img.copy() 
     if range is not None: 
         img = np.asfarray(img) 
         img -= mn 
         img *= b / (mx - mn) 
         img = np.clip(img, 0, b) 
     else: 
         if cutNegative: 
             img[img < 0] = 0 
         else: 
             mn = np.min(img) 
             if mn < 0: 
                 img -= mn  
         if cutHigh: 
             img[img > b] = b 
         else: 
             mx = np.nanmax(img) 
             img = np.asfarray(img) * (float(b) / mx) 
     img = img.astype(dtype) 
     return img

