def to_epoch(t):
     if isinstance(t, str):
         if "+" not in t:
             t = t + "+00:00"
         t = parser.parse(t)
     elif t.tzinfo is None or t.tzinfo.utcoffset(t) is None:
         t = t.replace(tzinfo=pytz.timezone("utc"))
     t0 = datetime.datetime(1970, 1, 1, 0, 0, 0, 0, pytz.timezone("utc"))
     delta = t - t0
     return int(delta.total_seconds())

def find_challenge_goal(self, id, raw=False):
         start = 0
         matching_strings = []
         def try_find():
             nonlocal start
             index = self.js_strings.index(id, start)
             for i in range(FIND_GOAL_SEARCH_DISTANCE):
                 next_str = self.js_strings[index + i + 1]
                 if re.search(r"\bselect all\b", next_str, re.I):
                     matching_strings.append((i, index, next_str))
             start = index + FIND_GOAL_SEARCH_DISTANCE + 1
         try:
             while True:
                 try_find()
         except (ValueError, IndexError):
             pass
         try:
             goal = min(matching_strings)[2]
         except ValueError:
             return None, None
         raw = goal
         plain = raw.replace("<strong>", "").replace("</strong>", "")
         return raw, plain

