def make_pdf_from_html(
         on_disk: bool,
         html: str,
         output_path: str = None,
         header_html: str = None,
         footer_html: str = None,
         wkhtmltopdf_filename: str = _WKHTMLTOPDF_FILENAME,
         wkhtmltopdf_options: Dict[str, Any] = None,
         file_encoding: str = "utf-8",
         debug_options: bool = False,
         debug_content: bool = False,
         debug_wkhtmltopdf_args: bool = True,
         fix_pdfkit_encoding_bug: bool = None,
         processor: str = _DEFAULT_PROCESSOR) -> Union[bytes, bool]:
     wkhtmltopdf_options = wkhtmltopdf_options or {}  
     assert_processor_available(processor)
     if debug_content:
         log.debug("html: {}", html)
         log.debug("header_html: {}", header_html)
         log.debug("footer_html: {}", footer_html)
     if fix_pdfkit_encoding_bug is None:
         fix_pdfkit_encoding_bug = get_default_fix_pdfkit_encoding_bug()
     if processor == Processors.XHTML2PDF:
         if on_disk:
             with open(output_path, mode="wb") as outfile:
                 xhtml2pdf.document.pisaDocument(html, outfile)
             return True
         else:
             memfile = io.BytesIO()
             xhtml2pdf.document.pisaDocument(html, memfile)
             memfile.seek(0)
             return memfile.read()
     elif processor == Processors.WEASYPRINT:
         if on_disk:
             return weasyprint.HTML(string=html).write_pdf(output_path)
         else:
             return weasyprint.HTML(string=html).write_pdf()
     elif processor == Processors.PDFKIT:
         if not wkhtmltopdf_filename:
             config = None
         else:
             if fix_pdfkit_encoding_bug:  
                 log.debug("Attempting to fix bug in pdfkit (e.g. version 0.5.0)"
                           " by encoding wkhtmltopdf_filename to UTF-8")
                 config = pdfkit.configuration(
                     wkhtmltopdf=wkhtmltopdf_filename.encode("utf-8"))
             else:
                 config = pdfkit.configuration(wkhtmltopdf=wkhtmltopdf_filename)
         h_filename = None
         f_filename = None
         try:
             if header_html:
                 h_fd, h_filename = tempfile.mkstemp(suffix=".html")
                 os.write(h_fd, header_html.encode(file_encoding))
                 os.close(h_fd)
                 wkhtmltopdf_options["header-html"] = h_filename
             if footer_html:
                 f_fd, f_filename = tempfile.mkstemp(suffix=".html")
                 os.write(f_fd, footer_html.encode(file_encoding))
                 os.close(f_fd)
                 wkhtmltopdf_options["footer-html"] = f_filename
             if debug_options:
                 log.debug("wkhtmltopdf config: {!r}", config)
                 log.debug("wkhtmltopdf_options: {}",
                           pformat(wkhtmltopdf_options))
             kit = pdfkit.pdfkit.PDFKit(html, "string", configuration=config,
                                        options=wkhtmltopdf_options)
             if on_disk:
                 path = output_path
             else:
                 path = None
             if debug_wkhtmltopdf_args:
                 log.debug("Probable current user: {!r}", getpass.getuser())
                 log.debug("wkhtmltopdf arguments will be: {!r}",
                           kit.command(path=path))
             return kit.to_pdf(path=path)
         finally:
             if h_filename:
                 os.remove(h_filename)
             if f_filename:
                 os.remove(f_filename)
     else:
         raise AssertionError("Unknown PDF engine")

def eeg_microstates_clustering(data, n_microstates=4, clustering_method="kmeans", n_jobs=1, n_init=25, occurence_rejection_treshold=0.05, max_refitting=5, verbose=True):
     training_set = data.copy()
     if verbose is True:
         print("- Initializing the clustering algorithm...")
     if clustering_method == "kmeans":
         algorithm = sklearn.cluster.KMeans(init="k-means++", n_clusters=n_microstates, n_init=n_init, n_jobs=n_jobs)
     elif clustering_method == "spectral":
         algorithm = sklearn.cluster.SpectralClustering(n_clusters=n_microstates, n_init=n_init, n_jobs=n_jobs)
     elif clustering_method == "agglom":
         algorithm = sklearn.cluster.AgglomerativeClustering(n_clusters=n_microstates, linkage="complete")
     elif clustering_method == "dbscan":
         algorithm = sklearn.cluster.DBSCAN(min_samples=100)
     elif clustering_method == "affinity":
         algorithm = sklearn.cluster.AffinityPropagation(damping=0.5)
     else:
         print("NeuroKit Error: eeg_microstates(): clustering_method must be "kmeans", "spectral", "dbscan", "affinity" or "agglom"")
     refitting = 0  
     good_fit_achieved = False
     while good_fit_achieved is False:
         good_fit_achieved = True
         if verbose is True:
             print("- Fitting the classifier...")
         algorithm.fit(training_set)
         if verbose is True:
             print("- Clustering back the initial data...")
         predicted = algorithm.fit_predict(training_set)
         if verbose is True:
             print("- Check for abnormalities...")
         occurences = dict(collections.Counter(predicted))
         masks = [np.array([True]*len(training_set))]
         for microstate in occurences:
             if occurences[microstate] < len(data)*occurence_rejection_treshold:
                 good_fit_achieved = False
                 refitting += 1  
                 print("NeuroKit Warning: eeg_microstates(): detected some outliers: refitting the classifier (n=" + str(refitting) + ").")
                 masks.append(predicted!=microstate)
         mask = np.all(masks, axis=0)
         training_set = training_set[mask]
     return(algorithm)

def datetime_from_iso8601(date):
     format = ISO8610_FORMAT
     if date.endswith("Z"):
         date = date[:-1]  
     if re.match(".*\.\d+", date):
         format = ISO8610_FORMAT_MICROSECONDS
     return datetime.datetime.strptime(date, format)

def optimize(self, x0, f=None, df=None, f_df=None):
         import scipy.optimize
         if f_df is None and df is not None: f_df = lambda x: float(f(x)), df(x)
         if f_df is not None:
             def _f_df(x):
                 return f(x), f_df(x)[1][0]
         if f_df is None and df is None:
             res = scipy.optimize.fmin_l_bfgs_b(f, x0=x0, bounds=self.bounds,approx_grad=True, maxiter=self.maxiter)
         else:
             res = scipy.optimize.fmin_l_bfgs_b(_f_df, x0=x0, bounds=self.bounds, maxiter=self.maxiter)
         if res[2]["task"] == b"ABNORMAL_TERMINATION_IN_LNSRCH":
             result_x  = np.atleast_2d(x0)
             result_fx =  np.atleast_2d(f(x0))
         else:
             result_x = np.atleast_2d(res[0])
             result_fx = np.atleast_2d(res[1])
         return result_x, result_fx

