         now = datetime.now().date()
         val = self.get_value(item, source_name)
         week_day, day = val.split()
         day = int(day)
         if now.day < day:
             if now.month == 1:
                 now = now.replace(month=12, year=now.year-1)
             else:
                 now = now.replace(month=now.month-1)
         now = now.replace(day=day)
         return now

