def find(value, find, start=0):
     l = len(value)
     if is_list(find):
         m = l
         for f in find:
             i = value.find(f, start)
             if i == -1:
                 continue
             m = min(m, i)
         return m
     else:
         i = value.find(find, start)
         if i == -1:
             return l
         return i

