             if entry[0] == xhtml2pdf.default.PML_ERROR:
                 logger_x2p.error("line %s, msg: %s, fragment: %s", entry[1], entry[2], entry[3])
         raise PDFRenderingError("Errors rendering PDF", content=content, log=pdf.log)
     if pdf.warn:
