def to_uint8(self, data):
         np = util.get_module(
             "numpy", required="wandb.Image requires numpy if not supplying PIL Images: pip install numpy")
         dmin = np.min(data)
         if dmin < 0:
             data = (data - np.min(data)) / np.ptp(data)
