def encrypt(self, raw, mode=AES.MODE_CBC):
         raw = self._pad(raw, AES.block_size)
         iv = Random.new().read(AES.block_size)
         cipher = AES.new(self.key, mode, iv)
         return base64.b64encode(iv + cipher.encrypt(raw)).decode("utf-8")

def groups_count(self):
         if self._keyboard_description.contents.ctrls is not None:
             return self._keyboard_description.contents.ctrls.contents.num_groups
         else:
             groups_source = self._groups_source
             groups_count = 0
             while (groups_count < XkbNumKbdGroups and
                    groups_source[groups_count] != None_):
                 groups_count += 1
             return groups_count

def Enum(name, *fields):
 	obj = TypeObject()
 	obj.isEnum = True
 	obj.__name__ = name
 	obj.__enum__ = {}
 	for a, p in fields:
 		obj.__enum__[p] = TypeObject()
 		obj.__enum__[p].name = a
 	return obj

