def boolval(v): 
     if isinstance(v, bool): 
         return v 
     if isinstance(v, int): 
         return bool(v) 
     if is_string(v): 
         v = v.lower() 
         if v in {"j", "y", "ja", "yes", "1", "true"}: 
             return True 
         if v in {"n", "nei", "no", "0", "false"}: 
             return False 
     raise ValueError("Don"t know how to convert %r to bool" % v)

