    for root, dirs, files in os.walk(directory, topdown=False):
       if root != "":
          orig_dir = os.getcwd()
          os.chdir(directory)
          Popen("gunzip -q -f *.gz > /dev/null 2>&1", shell=True).wait()
          Popen("unzip -qq -o "*.zip" > /dev/null 2>&1", shell=True).wait()
