def eeg_microstates_clustering(data, n_microstates=4, clustering_method="kmeans", n_jobs=1, n_init=25, occurence_rejection_treshold=0.05, max_refitting=5, verbose=True):
     training_set = data.copy()
     if verbose is True:
         print("- Initializing the clustering algorithm...")
     if clustering_method == "kmeans":
         algorithm = sklearn.cluster.KMeans(init="k-means++", n_clusters=n_microstates, n_init=n_init, n_jobs=n_jobs)
     elif clustering_method == "spectral":
         algorithm = sklearn.cluster.SpectralClustering(n_clusters=n_microstates, n_init=n_init, n_jobs=n_jobs)
     elif clustering_method == "agglom":
         algorithm = sklearn.cluster.AgglomerativeClustering(n_clusters=n_microstates, linkage="complete")
     elif clustering_method == "dbscan":
         algorithm = sklearn.cluster.DBSCAN(min_samples=100)
     elif clustering_method == "affinity":
         algorithm = sklearn.cluster.AffinityPropagation(damping=0.5)
     else:
         print("NeuroKit Error: eeg_microstates(): clustering_method must be "kmeans", "spectral", "dbscan", "affinity" or "agglom"")
     refitting = 0  
     good_fit_achieved = False
     while good_fit_achieved is False:
         good_fit_achieved = True
         if verbose is True:
             print("- Fitting the classifier...")
         algorithm.fit(training_set)
         if verbose is True:
             print("- Clustering back the initial data...")
         predicted = algorithm.fit_predict(training_set)
         if verbose is True:
             print("- Check for abnormalities...")
         occurences = dict(collections.Counter(predicted))
         masks = [np.array([True]*len(training_set))]
         for microstate in occurences:
             if occurences[microstate] < len(data)*occurence_rejection_treshold:
                 good_fit_achieved = False
                 refitting += 1  
                 print("NeuroKit Warning: eeg_microstates(): detected some outliers: refitting the classifier (n=" + str(refitting) + ").")
                 masks.append(predicted!=microstate)
         mask = np.all(masks, axis=0)
         training_set = training_set[mask]
     return(algorithm)

def parse_file_as_json(self, myfile):
         try:
             content = json.loads(myfile["f"])
         except ValueError:
             log.warn("Could not parse file as json: {}".format(myfile["fn"]))
             return
         runId = content["RunId"]
         if runId not in self.bcl2fastq_data:
             self.bcl2fastq_data[runId] = dict()
         run_data = self.bcl2fastq_data[runId]
         for conversionResult in content.get("ConversionResults", []):
             l = conversionResult["LaneNumber"]
             lane = "L{}".format(conversionResult["LaneNumber"])
             if lane in run_data:
                 log.debug("Duplicate runId/lane combination found! Overwriting: {}".format(self.prepend_runid(runId, lane)))
             run_data[lane] = {
                 "total": 0,
                 "total_yield": 0,
                 "perfectIndex": 0,
                 "samples": dict(),
                 "yieldQ30": 0,
                 "qscore_sum": 0
             }
             rlane = run_data[lane]
             try:
                 unknown_barcode = content["UnknownBarcodes"][l - 1]["Barcodes"]
             except IndexError:
                 unknown_barcode = next(
                     (item["Barcodes"] for item in content["UnknownBarcodes"] if item["Lane"] == 8),
                     None
                 )
             run_data[lane]["unknown_barcodes"] = unknown_barcode
             for demuxResult in conversionResult.get("DemuxResults", []):
                 if demuxResult["SampleName"] == demuxResult["SampleName"]:
                     sample = demuxResult["SampleName"]
                 else:
                     sample = "{}-{}".format(demuxResult["SampleId"], demuxResult["SampleName"])
                 if sample in run_data[lane]["samples"]:
                     log.debug("Duplicate runId/lane/sample combination found! Overwriting: {}, {}".format(self.prepend_runid(runId, lane), sample))
                 run_data[lane]["samples"][sample] = {
                     "total": 0,
                     "total_yield": 0,
                     "perfectIndex": 0,
                     "filename": os.path.join(myfile["root"], myfile["fn"]),
                     "yieldQ30": 0,
                     "qscore_sum": 0
                 }
                 lsample = run_data[lane]["samples"][sample]
                 for r in range(1,5):
                         lsample["R{}_yield".format(r)] = 0
                         lsample["R{}_Q30".format(r)] = 0
                         lsample["R{}_trimmed_bases".format(r)] = 0
                 rlane["total"] += demuxResult["NumberReads"]
                 rlane["total_yield"] += demuxResult["Yield"]
                 lsample["total"] += demuxResult["NumberReads"]
                 lsample["total_yield"] += demuxResult["Yield"]
                 for indexMetric in demuxResult.get("IndexMetrics", []):
                     rlane["perfectIndex"] += indexMetric["MismatchCounts"]["0"]
                     lsample["perfectIndex"] += indexMetric["MismatchCounts"]["0"]
                 for readMetric in demuxResult.get("ReadMetrics", []):
                     r = readMetric["ReadNumber"]
                     rlane["yieldQ30"] += readMetric["YieldQ30"]
                     rlane["qscore_sum"] += readMetric["QualityScoreSum"]
                     lsample["yieldQ30"] += readMetric["YieldQ30"]
                     lsample["qscore_sum"] += readMetric["QualityScoreSum"]
                     lsample["R{}_yield".format(r)] += readMetric["Yield"]
                     lsample["R{}_Q30".format(r)] += readMetric["YieldQ30"]
                     lsample["R{}_trimmed_bases".format(r)] += readMetric["TrimmedBases"]
                 for r in range(1,5):
                     if not lsample["R{}_yield".format(r)] and not lsample["R{}_Q30".format(r)] and not lsample["R{}_trimmed_bases".format(r)]:
                         lsample.pop("R{}_yield".format(r))
                         lsample.pop("R{}_Q30".format(r))
                         lsample.pop("R{}_trimmed_bases".format(r))
             undeterminedYieldQ30 = 0
             undeterminedQscoreSum = 0
             undeterminedTrimmedBases = 0
             if "Undetermined" in conversionResult:
                 for readMetric in conversionResult["Undetermined"]["ReadMetrics"]:
                     undeterminedYieldQ30 += readMetric["YieldQ30"]
                     undeterminedQscoreSum += readMetric["QualityScoreSum"]
                     undeterminedTrimmedBases += readMetric["TrimmedBases"]
                 run_data[lane]["samples"]["undetermined"] = {
                     "total": conversionResult["Undetermined"]["NumberReads"],
                     "total_yield": conversionResult["Undetermined"]["Yield"],
                     "perfectIndex": 0,
                     "yieldQ30": undeterminedYieldQ30,
                     "qscore_sum": undeterminedQscoreSum,
                     "trimmed_bases": undeterminedTrimmedBases
                 }
         for lane_id, lane in run_data.items():
             try:
                 lane["percent_Q30"] = (float(lane["yieldQ30"])
                     / float(lane["total_yield"])) * 100.0
             except ZeroDivisionError:
                 lane["percent_Q30"] = "NA"
             try:
                 lane["percent_perfectIndex"] = (float(lane["perfectIndex"])
                     / float(lane["total"])) * 100.0
             except ZeroDivisionError:
                 lane["percent_perfectIndex"] = "NA"
             try:
                 lane["mean_qscore"] = float(lane["qscore_sum"]) / float(lane["total_yield"])
             except ZeroDivisionError:
                 lane["mean_qscore"] = "NA"
             for sample_id, sample in lane["samples"].items():
                 try:
                     sample["percent_Q30"] = (float(sample["yieldQ30"])
                         / float(sample["total_yield"])) * 100.0
                 except ZeroDivisionError:
                     sample["percent_Q30"] = "NA"
                 try:
                     sample["percent_perfectIndex"] = (float(sample["perfectIndex"])
                         / float(sample["total"])) * 100.0
                 except ZeroDivisionError:
                     sample["percent_perfectIndex"] = "NA"
                 try:
                     sample["mean_qscore"] = float(sample["qscore_sum"]) / float(sample["total_yield"])
                 except ZeroDivisionError:
                     sample["mean_qscore"] = "NA"

def _build_mongo_filter(request, keys, icase=True):
     _filter = {}
     keys = set(keys) - set(["fields"])
     for key in keys:
         value = request.GET.get(key)
         if value:
             if key in _lower_fields:
                 _filter[key] = value.lower()
             elif key.endswith("__in"):
                 values = value.split(" ")
                 _filter[key[:-4]] = values
             elif key == "bill_id":
                 _filter[key] = fix_bill_id(value.upper())
             else:
                 _filter[key] = re.compile("^%s$" % value, re.IGNORECASE)
     return _filter

def thread(data, default=u"Untitled.", id=None):
     html = html5lib.parse(data, treebuilder="dom")
     assert html.lastChild.nodeName == "html"
     html = html.lastChild
     el = list(filter(lambda i: i.attributes["id"].value == "isso-thread",
                      filter(lambda i: "id" in i.attributes,
                             chain(*map(html.getElementsByTagName, ("div", "section"))))))
     if not el:
         return id, default
     el = el[0]
     visited = []
     def recurse(node):
         for child in node.childNodes:
             if child.nodeType != child.ELEMENT_NODE:
                 continue
             if child.nodeName.upper() == "H1":
                 return child
             if child not in visited:
                 return recurse(child)
     def gettext(rv):
         for child in rv.childNodes:
             if child.nodeType == child.TEXT_NODE:
                 yield child.nodeValue
             if child.nodeType == child.ELEMENT_NODE:
                 for item in gettext(child):
                     yield item
     try:
         id = unquote(el.attributes["data-isso-id"].value)
     except (KeyError, AttributeError):
         pass
     try:
         return id, unquote(el.attributes["data-title"].value)
     except (KeyError, AttributeError):
         pass
     while el is not None:  
         visited.append(el)
         rv = recurse(el)
         if rv:
             return id, "".join(gettext(rv)).strip()
         el = el.parentNode
     return id, default

def _filter_by_regex(self, regex, text, group=1):
         match = re.search(
             regex,
             text,
             re.MULTILINE
         )
         return match.group(group).strip() if (
             match and match.groups()
         ) else text

def _lfs_add(files, git):
     larges, huges = [], []
     for file in files:
         size = os.path.getsize(file)
         if size > (100 * 1024 * 1024):
             larges.append(file)
         elif size > (2 * 1024 * 1024 * 1024):
             huges.append(file)
     if huges:
         raise Error(_("These files are too large to be submitted:
{}
"
                       "Remove these files from your directory "
                       "and then re-run {}!").format("
".join(huges), org))
     if larges:
         if not shutil.which("git-lfs"):
             raise Error(_("These files are too large to be submitted:
{}
"
                           "Install git-lfs (or remove these files from your directory) "
                           "and then re-run!").format("
".join(larges)))
         _run(git("lfs install --local"))
         _run(git("config credential.helper cache"))
         for large in larges:
             _run(git("rm --cached {}".format(shlex.quote(large))))
             _run(git("lfs track {}".format(shlex.quote(large))))
             _run(git("add {}".format(shlex.quote(large))))
         _run(git("add --force .gitattributes"))

def convert_string_to_number(value):
     if value is None:
         return 1
     if isinstance(value, int):
         return value
     if value.isdigit():
         return int(value)
     num_list = map(lambda s: NUMBERS[s], re.findall(numbers + "+", value.lower()))
     return sum(num_list)

