def memoize(func):
     cache = func._util_decor_memoize_cache = {}
     def memoizer(*args, **kwargs):
         key = str(args) + str(kwargs)
         if key not in cache:
             cache[key] = func(*args, **kwargs)
         return cache[key]
     memoizer = preserve_sig(memoizer, func)
     memoizer.cache = cache
     return memoizer

