             output_file_path,
             parameters=None
     ):
         if not gf.file_can_be_written(output_file_path):
             self.log_exc(u"Cannot output HTML file "%s". Wrong permissions?" % (output_file_path), None, True, OSError)
         if parameters is None:
             parameters = {}
         audio_file_path_absolute = gf.fix_slash(os.path.abspath(audio_file_path))
         template_path_absolute = gf.absolute_path(self.FINETUNEAS_PATH, __file__)
         with io.open(template_path_absolute, "r", encoding="utf-8") as file_obj:
             template = file_obj.read()
         for repl in self.FINETUNEAS_REPLACEMENTS:
             template = template.replace(repl[0], repl[1])
         template = template.replace(
             self.FINETUNEAS_REPLACE_AUDIOFILEPATH,
             u"audioFilePath = \"file://%s\";" % audio_file_path_absolute
         )
         template = template.replace(
             self.FINETUNEAS_REPLACE_FRAGMENTS,
             u"fragments = (%s).fragments;" % self.json_string
         )
         if gc.PPN_TASK_OS_FILE_FORMAT in parameters:
             output_format = parameters[gc.PPN_TASK_OS_FILE_FORMAT]
             if output_format in self.FINETUNEAS_ALLOWED_FORMATS:
                 template = template.replace(
                     self.FINETUNEAS_REPLACE_OUTPUT_FORMAT,
                     u"outputFormat = \"%s\";" % output_format
                 )
                 if output_format == "smil":
                     for key, placeholder, replacement in [
                             (
                                 gc.PPN_TASK_OS_FILE_SMIL_AUDIO_REF,
                                 self.FINETUNEAS_REPLACE_SMIL_AUDIOREF,
                                 "audioref = \"%s\";"
                             ),
                             (
                                 gc.PPN_TASK_OS_FILE_SMIL_PAGE_REF,
                                 self.FINETUNEAS_REPLACE_SMIL_PAGEREF,
                                 "pageref = \"%s\";"
                             ),
                     ]:
                         if key in parameters:
                             template = template.replace(
                                 placeholder,
                                 replacement % parameters[key]
                             )
         with io.open(output_file_path, "w", encoding="utf-8") as file_obj:
             file_obj.write(template)

def perform_filter(result_array: Array, data, f_low, f_high, filter_bw):
    result_array = np.frombuffer(result_array.get_obj(), dtype=np.complex64)
    result_array[:] = Filter.apply_bandpass_filter(data, f_low, f_high, filter_bw=filter_bw)

def _to_string(self):
         enum_name = None
         value = self._get_calculated_value(self.value)
         for enum, enum_value in vars(self.enum_type).items():
             if value == enum_value:
                 enum_name = enum
                 break
         if enum_name is None:
             return "(%d) UNKNOWN_ENUM" % value
         else:
             return "(%d) %s" % (value, enum_name)

def read_next(self):
         if not self.read_buffer:
             self._read_next_batch()
             assert self.read_buffer
         self.read_item_offset += 1
         return self.read_buffer.pop(0)

def _file_size(file_path, uncompressed=False):
     _, ext = os.path.splitext(file_path)
     if uncompressed:
         if ext in {".gz", ".gzip"}:
             with gzip.GzipFile(file_path, mode="rb") as fp:
                 try:
                     fp.seek(0, os.SEEK_END)
                     return fp.tell()
                 except ValueError:
                     fp.seek(0)
                     while len(fp.read(8192)) != 0:
                         pass
                     return fp.tell()
         elif ext in {".bz", ".bz2", ".bzip", ".bzip2"}:
             with bz2.BZ2File(file_path, mode="rb") as fp:
                 fp.seek(0, os.SEEK_END)
                 return fp.tell()
     return os.path.getsize(file_path)

def parse_query(query, ilogger):
     p = urlparse(query)
     if p and p.scheme and p.netloc:
         if "youtube" in p.netloc and p.query and ytdiscoveryapi is not None:
             query_parts = p.query.split("&")
             yturl_parts = {}
             for q in query_parts:
                 s = q.split("=")
                 if len(s) < 2:
                     continue
                 q_name = s[0]
                 q_val = "=".join(s[1:])
                 if q_name not in yturl_parts:
                     yturl_parts[q_name] = q_val
             if "list" in yturl_parts:
                 ilogger.info("Queued YouTube playlist from link")
                 return get_queue_from_playlist(yturl_parts["list"])
             elif "v" in yturl_parts:
                 ilogger.info("Queued YouTube video from link")
                 return [["https://www.youtube.com/watch?v={}".format(yturl_parts["v"]), query]]
         elif "soundcloud" in p.netloc:
             if scclient is None:
                 ilogger.error("Could not queue from SoundCloud API, using link")
                 return [[query, query]]
             try:
                 result = scclient.get("/resolve", url=query)
                 track_list = []
                 if isinstance(result, ResourceList):
                     for r in result.data:
                         tracks = get_sc_tracks(r)
                         if tracks is not None:
                             for t in tracks:
                                 track_list.append(t)
                 elif isinstance(result, Resource):
                     tracks = get_sc_tracks(result)
                     if tracks is not None:
                         for t in tracks:
                             track_list.append(t)
                 if track_list is not None and len(track_list) > 0:
                     ilogger.info("Queued SoundCloud songs from link")
                     return track_list
                 else:
                     ilogger.error("Could not queue from SoundCloud API")
                     return [[query, query]]
             except Exception as e:
                 logger.exception(e)
                 ilogger.error("Could not queue from SoundCloud API, using link")
                 return [[query, query]]
         else:
             ilogger.debug("Using url: {}".format(query))
             return [[query, query]]
     args = query.split(" ")
     if len(args) == 0:
         ilogger.error("No query given")
         return []
     if args[0].lower() in ["sp", "spotify"] and spclient is not None:
         if spclient is None:
             ilogger.error("Host does not support Spotify")
             return []
         try:
             if len(args) > 2 and args[1] in ["album", "artist", "song", "track", "playlist"]:
                 query_type = args[1].lower()
                 query_search = " ".join(args[2:])
             else:
                 query_type = "track"
                 query_search = " ".join(args[1:])
             query_type = query_type.replace("song", "track")
             ilogger.info("Queueing Spotify {}: {}".format(query_type, query_search))
             spotify_tracks = search_sp_tracks(query_type, query_search)
             if spotify_tracks is None or len(spotify_tracks) == 0:
                 ilogger.error("Could not queue Spotify {}: {}".format(query_type, query_search))
                 return []
             ilogger.info("Queued Spotify {}: {}".format(query_type, query_search))
             return spotify_tracks
         except Exception as e:
             logger.exception(e)
             ilogger.error("Error queueing from Spotify")
             return []
     elif args[0].lower() in ["sc", "soundcloud"]:
         if scclient is None:
             ilogger.error("Host does not support SoundCloud")
             return []
         try:
             requests = ["song", "songs", "track", "tracks", "user", "playlist", "tagged", "genre"]
             if len(args) > 2 and args[1] in requests:
                 query_type = args[1].lower()
                 query_search = " ".join(args[2:])
             else:
                 query_type = "track"
                 query_search = " ".join(args[1:])
             query_type = query_type.replace("song", "track")
             ilogger.info("Queueing SoundCloud {}: {}".format(query_type, query_search))
             soundcloud_tracks = search_sc_tracks(query_type, query_search)
             ilogger.info("Queued SoundCloud {}: {}".format(query_type, query_search))
             return soundcloud_tracks
         except Exception as e:
             logger.exception(e)
             ilogger.error("Could not queue from SoundCloud")
             return []
     elif args[0].lower() in ["yt", "youtube"] and ytdiscoveryapi is not None:
         if ytdiscoveryapi is None:
             ilogger.error("Host does not support YouTube")
             return []
         try:
             query_search = " ".join(args[1:])
             ilogger.info("Queued Youtube search: {}".format(query_search))
             return get_ytvideos(query_search, ilogger)
         except Exception as e:
             logger.exception(e)
             ilogger.error("Could not queue YouTube search")
             return []
     if ytdiscoveryapi is not None:
         ilogger.info("Queued YouTube search: {}".format(query))
         return get_ytvideos(query, ilogger)
     else:
         ilogger.error("Host does not support YouTube".format(query))
         return []

def find_executable(executable: str, *paths: str) -> typing.Optional[Path]:
     if not executable.endswith(".exe"):
         executable = f"{executable}.exe"
     if executable in _KNOWN_EXECUTABLES:
         return _KNOWN_EXECUTABLES[executable]
     output = f"{executable}"
     if not paths:
         path = os.environ["PATH"]
         paths = tuple([str(Path(sys.exec_prefix, "Scripts").absolute())] + path.split(os.pathsep))
     executable_path = Path(executable).absolute()
     if not executable_path.is_file():
         for path_ in paths:
             executable_path = Path(path_, executable).absolute()
             if executable_path.is_file():
                 break
         else:
             _LOGGER.error("%s -> not found", output)
             return None
     _KNOWN_EXECUTABLES[executable] = executable_path
     _LOGGER.info("%s -> %s", output, str(executable_path))
     return executable_path

