def compress_file(fh_, compresslevel=9, chunk_size=1048576):
     try:
         bytes_read = int(chunk_size)
         if bytes_read != chunk_size:
             raise ValueError
     except ValueError:
         raise ValueError("chunk_size must be an integer")
     try:
         while bytes_read == chunk_size:
             buf = BytesIO()
             with open_fileobj(buf, "wb", compresslevel) as ogz:
                 try:
                     bytes_read = ogz.write(fh_.read(chunk_size))
                 except AttributeError:
                     fh_ = salt.utils.files.fopen(fh_, "rb")
                     bytes_read = ogz.write(fh_.read(chunk_size))
             yield buf.getvalue()
     finally:
         try:
             fh_.close()
         except AttributeError:
             pass

def find_challenge_goal(self, id, raw=False):
         start = 0
         matching_strings = []
         def try_find():
             nonlocal start
             index = self.js_strings.index(id, start)
             for i in range(FIND_GOAL_SEARCH_DISTANCE):
                 next_str = self.js_strings[index + i + 1]
                 if re.search(r"\bselect all\b", next_str, re.I):
                     matching_strings.append((i, index, next_str))
             start = index + FIND_GOAL_SEARCH_DISTANCE + 1
         try:
             while True:
                 try_find()
         except (ValueError, IndexError):
             pass
         try:
             goal = min(matching_strings)[2]
         except ValueError:
             return None, None
         raw = goal
         plain = raw.replace("<strong>", "").replace("</strong>", "")
         return raw, plain

def parse_multiple_json(json_file, offset=None):
     json_info_list = []
     if not os.path.exists(json_file):
         return json_info_list
     try:
         with open(json_file, "r") as f:
             if offset:
                 f.seek(offset)
             for line in f:
                 if line[-1] != "
":
                     break
                 json_info = json.loads(line)
                 json_info_list.append(json_info)
                 offset += len(line)
     except BaseException as e:
         logging.error(e.message)
     return json_info_list, offset

def thread(data, default=u"Untitled.", id=None):
     html = html5lib.parse(data, treebuilder="dom")
     assert html.lastChild.nodeName == "html"
     html = html.lastChild
     el = list(filter(lambda i: i.attributes["id"].value == "isso-thread",
                      filter(lambda i: "id" in i.attributes,
                             chain(*map(html.getElementsByTagName, ("div", "section"))))))
     if not el:
         return id, default
     el = el[0]
     visited = []
     def recurse(node):
         for child in node.childNodes:
             if child.nodeType != child.ELEMENT_NODE:
                 continue
             if child.nodeName.upper() == "H1":
                 return child
             if child not in visited:
                 return recurse(child)
     def gettext(rv):
         for child in rv.childNodes:
             if child.nodeType == child.TEXT_NODE:
                 yield child.nodeValue
             if child.nodeType == child.ELEMENT_NODE:
                 for item in gettext(child):
                     yield item
     try:
         id = unquote(el.attributes["data-isso-id"].value)
     except (KeyError, AttributeError):
         pass
     try:
         return id, unquote(el.attributes["data-title"].value)
     except (KeyError, AttributeError):
         pass
     while el is not None:  
         visited.append(el)
         rv = recurse(el)
         if rv:
             return id, "".join(gettext(rv)).strip()
         el = el.parentNode
     return id, default

