def datestr2date(date_str):
     if any(c not in "0123456789-/" for c in date_str):
         raise ValueError("Illegal character in date string")
     if "/" in date_str:
         try:
             m, d, y = date_str.split("/")
         except:
             raise ValueError("Date {} must have no or exactly 2 slashes. {}".
                     format(date_str, VALID_DATE_FORMATS_TEXT))
     elif "-" in date_str:
         try:
             d, m, y = date_str.split("-")
         except:
             raise ValueError("Date {} must have no or exactly 2 dashes. {}".
                     format(date_str, VALID_DATE_FORMATS_TEXT))
     elif len(date_str) == 8 or len(date_str) == 6:
         d = date_str[-2:]
         m = date_str[-4:-2]
         y = date_str[:-4]
     else:
         raise ValueError("Date format not recognised. {}".format(
                 VALID_DATE_FORMATS_TEXT))
     if len(y) == 2:
         year = 2000 + int(y)
     elif len(y) == 4:
         year = int(y)
     else:
         raise ValueError("year must be 2 or 4 digits")
     for s in (m, d):
         if 1 <= len(s) <= 2:
             month, day = int(m), int(d)
         else:
             raise ValueError("m and d must be 1 or 2 digits")
     try:
         return datetime.date(year, month, day)
     except ValueError:
         raise ValueError("Invalid date {}. {}".format(date_str, 
                 VALID_DATE_FORMATS_TEXT))

def read_csv(self, file_path, use_whole_file=False, names=None, skiprows=0,
                  *args, **kwargs):
         def csv_file(partition_number, files):
             file_count = 0
             for _, contents in files:
                 if partition_number == 0 and file_count == 0 and _skiprows > 0:
                     yield pandas.read_csv(
                         sio(contents), *args,
                         header=None,
                         names=mynames,
                         skiprows=_skiprows,
                         **kwargs)
                 else:
                     file_count += 1
                     yield pandas.read_csv(
                         sio(contents), *args,
                         header=None,
                         names=mynames,
                         **kwargs)
         def csv_rows(partition_number, rows):
             in_str = "
".join(rows)
             if partition_number == 0:
                 return iter([
                     pandas.read_csv(
                         sio(in_str), *args, header=None,
                         names=mynames,
                         skiprows=_skiprows,
                         **kwargs)])
             else:
                 return iter([pandas.read_csv(sio(in_str), *args, header=None,
                                              names=mynames, **kwargs)])
         mynames = None
         _skiprows = skiprows
         if names:
             mynames = names
         else:
             first_line = self.spark_ctx.textFile(file_path).first()
             frame = pandas.read_csv(sio(first_line), **kwargs)
             mynames = list(frame.columns)
             _skiprows += 1
         if use_whole_file:
             return self.from_pandas_rdd(
                 self.spark_ctx.wholeTextFiles(file_path)
                 .mapPartitionsWithIndex(csv_file))
         else:
             return self.from_pandas_rdd(
                 self.spark_ctx.textFile(file_path)
                     .mapPartitionsWithIndex(csv_rows))

