             ddtt = {}
             for element in itt:
                 if len(element.split()) == 0:
                     break
                 ddtt[element.split()[0].lower()] = []
             for element in itt:
                 if len(element.split()) == 0:
                     break
                 ddtt[element.split()[0].lower()].append(" ".join(element.split()[1:]))
             alist.append(ddtt)
         lss.append(alist)
     commdct = lss
     return blocklst, commlst, commdct

