     scatter = _add_scatter_plot(out, data)
     if scatter:
         out["plot"]["scatter"] = scatter
     scatter_global = _add_global_scatter_plot(out, data)
     if scatter_global:
         out["plot"]["scatter_global"] = scatter_global
     return out

def epoch(self):
         epoch_sec = pytz.utc.localize(datetime.utcfromtimestamp(0))
         now_sec = pytz.utc.normalize(self._dt)
         delta_sec = now_sec - epoch_sec
         return get_total_second(delta_sec)

def table_metadata(self, database, table):
         "Fetch table-specific metadata."
         return (self.metadata("databases") or {}).get(database, {}).get(
             "tables", {}
         ).get(
             table, {}
         )

def Binomial(n, p, tag=None):
     assert (
         int(n) == n and n > 0
     ), "Binomial number of trials "n" must be an integer greater than zero"
     assert (
         0 < p < 1
     ), "Binomial probability "p" must be between zero and one, non-inclusive"
     return uv(ss.binom(n, p), tag=tag)

def convert_to_hex_string(string):
     hex_str = ""
     for c in string:
         hex_tmp = hex(ord(c))[2:]
         if len(hex_tmp) == 1:
             hex_tmp = "0" + hex_tmp
         hex_str += hex_tmp
     return hex_str.upper()
